<!DOCTYPE HTML PUBLIC "-//W3C//DTD HTML 4.01 Transitional//EN" "http://www.w3.org/TR/html4/loose.dtd"><html><head><title>Connection Test</title><meta http-equiv="Content-Type" content="text/html; charset=iso-8859-1"></head><body><?php	//CONNECT TO DATABASE	// USe your own details in the next line	$dbc = mysql_connect("silva.computing.dundee.ac.uk","xxxxxxxx","xxxxxx");		if (!$dbc)	{		die("Could not connect: " . mysql_error());	}	else	{		echo "CONNECTED!<br>";	}		//SELECT DATABASE	$q = mysql_select_db("xxxxxxxxx");	if ($q)	{		echo "DB SELECTED";	}	else	{		echo "ERROR SELECTING DB: " . mysql_error();	}				//CLOSE CONNECTION	mysql_close($dbc);	?></body></html>