<!DOCTYPE HTML PUBLIC "-//W3C//DTD HTML 4.01 Transitional//EN" "http://www.w3.org/TR/html4/loose.dtd"><html><head><title>MsC PHP</title><meta http-equiv="Content-Type" content="text/html; charset=iso-8859-1"></head><body><?php	//CONNECT TO DATABASE	$dbc = mysql_connect("arlia","09mscgp7","123abc");		if (!$dbc)	{		die("Could not connect: " . mysql_error());	}	else	{		echo "CONNECTED!<br>";	}		//SELECT DATABASE	$q = mysql_select_db("09mscdb7",$dbc);	if ($q)	{		echo "DB SELECTED";	}	else	{		echo "ERROR SELECTING DB: " . mysql_error();	}		//CREATE TABLE	$sqlCreate = "CREATE TABLE People	(		FirstName varchar(20),		LastName varchar(20),		id int,		PRIMARY KEY (id)	)";			if (mysql_query($sqlCreate,$dbc))	{		echo "<br> table created";	}		else	{		echo "ERROR \creating table: " . mysql_error();	}		//CLOSE CONNECTION	mysql_close($dbc);	?></body></html>