<?php$first = $_POST['firstname'];$last = $_POST['lastname'];$id = $_POST['id'];$house = $_POST['house'];$street = $_POST['street'];include 'dbConnect.php';$sqlInsert1 = "INSERT INTO persons (FirstName, LastName, Pid) VALUES ('$first', '$last', '$id')";if (!mysql_query($sqlInsert1))  {  die('Error: ' . mysql_error());  }echo "new person added<br>";$sqlInsert2 = "INSERT INTO address (number, street, Pid) VALUES ('$house', '$street', '$id')";if (!mysql_query($sqlInsert2))  {  die('Error: ' . mysql_error());  }echo "Person's Address added";?>